import random
class Persona:
    def __init__(self, nome,cognome):
        self.nome=nome
        self.cognome=cognome

    def __repr__(self):
        return f"{self.nome} {self.cognome}"

class cBancario(Persona):
    def __init__(self,nome,cognome):
        super().__init__(nome,cognome)
        self.nConto=random.randint(1000,9999)
        self.saldo=0

    def __repr__(self):
        return f"il conto {self.nConto} Ã¨ della persona {super().__repr__()} ed ha un saldo di {round(self.saldo,2)} euro"

    def aggiungiSoldi(self,soldi):
        self.saldo+=soldi

    def preleva(self,soldi):

        if(self.saldo>soldi):
            self.saldo -= soldi

        else:print("imposssibile prelevare")

class cBancarioPlus(cBancario):
    def __init__(self,nome,cognome):
        super().__init__(nome, cognome)

    def __repr__(self):
        return f"{super().__repr__()}"

    def aggiungiSoldiPlus(self,soldi):
        soldi+=soldi/100
        self.saldo+=soldi

peronaNormale= cBancario("alfonzo","barattini")
print(peronaNormale)
peronaNormale.aggiungiSoldi(50)
