class Progetto3:
    def __init__(self, nome, cognome):
        self.nome = nome
        self.cognome = cognome
        print("Hai creato la classe giusta!")
        print(f"I parametri sono {nome} e {cognome}")

    def stampaInfo(self):
            print(f"Mi chiamo {self.nome} {self.cognome}")


oggetto_classe= Progetto3(input("come ti chiami?\n"),input("dimmi il cognome: "))

oggetto_classe.stampaInfo()