class Progetto9:
    def __init__(self, caratteristiche):
        self.NRuote = caratteristiche['NRuote']
        self.colore = caratteristiche['colore']

    def __repr__(self):
        return f"la sedia ha {self.NRuote} ruote ed è di colore {self.colore}\n"

    def confronto(self,ruote2):

        if(self.NRuote>ruote2):
            print("sedia Gialla è più stabile di sedia rossa")

        elif(self.NRuote==ruote2):
            print("sedia Gialla e sedia rossa sono stabili uguali")

        else:print("sedia rossa è più stabile di sedia gialla")

sediaRossa= Progetto9({'NRuote':4,'colore':"rosso"})

sediaGialla= Progetto9({'NRuote':int(input("quante ruote ho? ")),'colore':"gialla"})

sediaGialla.confronto(sediaRossa.NRuote)